    function leapYear (year){
        return ((year & 3) == 0 && ((year % 25) != 0 || (year & 15) == 0));
    }

    function OffTime (ueT, Os, dY){
        var DoM = [31,28,31,30,31,30,31,31,30,31,30,31];
        let vT = ueT;

        let vDateD = vT.getDate();
        let vDateM = vT.getMonth() + 1;
        let vDateY = vT.getFullYear();
        let DoMA = DoM[vDateM - 1];
        DoMA += leapYear(vDateY) ? 1 : 0;
        let vDateHo = vT.getHours();
        let vDateMi = vT.getMinutes();
if (vDateMi < 30) {
            vDateMi = 30;
        } else {
            vDateMi = 0;
            vDateHo = vDateHo + 1;
        }
        let HoOs = vDateHo + Os;
        let ueTH = HoOs >= 24 ? 1 : 0;
        HoOs -= ueTH ? 24 : 0;
        let vHo = '';
        vHo += HoOs < 10 ? '0' + HoOs : '' + HoOs;

        let DOs = vDateD + ueTH;
        let ueTD = (DOs > DoMA) ? 1 : 0;
        DOs = (DOs > DoMA) ? 1 : DOs;
        let vD = '';
        vD += DOs < 10 ? '0' + DOs : '' + DOs;

        let MOs = vDateM + ueTD;
        let ueTM = (MOs > 12) ? 1 : 0;
        MOs -= (MOs > 12) ? 12 : 0;
        let vM = '';
        vM += vDateM < 10 ? '0' + vDateM : '' + vDateM;

        let vY = vDateY + ueTM;

        let vMi = '';
        vMi += vDateMi < 10 ? '0' + vDateMi : '' + vDateMi;


        if (dY) {
            vD += '.' + vM + '.' + vY + ' / ' + vHo + ':' + vMi +' Uhr';
        } else {
            vD += '.' + vM + '. / ' + vHo + ':' + vMi +' Uhr';
        }

        return vD;
    }

